package mx.com.sintelti.lsanchez.Java;

public class Sentenci_If {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var=6;
		boolean cond;
		cond = true;
		if (var <= 10){
			cond = true;
			
		}
		else {
			cond = false;
			
		}
		if (cond){
			System.out.print("Tu operacion es verdadera");
		}
		else {
			System.out.print("Tu operacion es falsa ");
		}

	}

}
